const nav=document.getElementById("nav-header");const eventDate=new Date("Mar 12 2022 12:00:00 GMT+0000 (GMT)");const underlineTitles=[...document.querySelectorAll(".section h1")]
const state={headerTransparent:nav.classList.contains("nav-transparent"),};function scrollEvent(){const scroll=window.scrollY;if(scroll>=10){if(state.headerTransparent)nav.classList.remove("nav-transparent");state.headerTransparent=false;}else{if(!state.headerTransparent)nav.classList.add("nav-transparent");state.headerTransparent=true;}
underlineTitles.forEach(hdr=>{if(hdr.offsetTop-scroll<window.innerHeight*0.5){hdr.classList.add("underlined");}else{hdr.classList.remove("underlined");}});}
window.addEventListener("scroll",scrollEvent);scrollEvent();[...document.getElementsByClassName("countdown")].forEach(elem=>{const days=elem.children[0];const hours=elem.children[1];const minutes=elem.children[2];const update=()=>{const diff=Math.max(0,eventDate.valueOf()-Date.now());days.innerHTML=`${Math.floor(diff/1000/60/60/24)}`;hours.innerHTML=`${Math.floor(diff/1000/60/60)%24}`;minutes.innerHTML=`${Math.floor(diff/1000/60)%60}`;setTimeout(update,1000*60);};update();});[...document.querySelectorAll("#rand-order")].forEach(elems=>{for(let i=elems.children.length;i>=0;i--)
elems.appendChild(elems.children[Math.random()*i|0]);});[...document.querySelectorAll(".collapsible")].forEach(elem=>{elem.addEventListener("click",function(){this.classList.toggle("active");const content=this.nextElementSibling;if(content.style.maxHeight){content.style.maxHeight=null;}else{content.style.maxHeight=content.scrollHeight+"px";}});});